mapper.select:
SELECT m.SEQ, m.CONTENT, m.FROM, m.TO, m.SDATE, m.OPEN, m.DEL, m.IMPORTANT, m.DISTINGUISH, c.NAME FROM MESSAGE m, C_Member c WHERE m.FROM = c.SEQ AND m.DISTINGUISH = 'com' AND m.DEL = 0
AND( m.CONTENT LIKE CONCAT('%', #{sKeyword}, '%') OR c.NAME = #{sKeyword})
ORDER BY m.SDATE DESC LIMIT #{recordCountPerPage, jdbcType=INTEGER} OFFSET #{start, jdbcType=INTEGER}

mapper.update:
UPDATE MESSAGE
SET IMPORTANT = 1
WHERE SEQ = ${seq}

UPDATE MESSAGE
SET IMPORTANT = 0
WHERE SEQ = ${seq}

mapper.select:
SELECT m.SEQ, m.CONTENT, m.FROM, m.TO, m.SDATE, m.OPEN, m.DEL, m.IMPORTANT, c.NAME
FROM MESSAGE m, C_Member c
WHERE m.FROM = c.SEQ AND m.SEQ = ${seq}

SELECT m.SEQ, m.CONTENT, m.FROM, m.TO, m.SDATE, m.OPEN, m.DEL, m.IMPORTANT, m.DISTINGUISH, c.NAME FROM MESSAGE m, C_Member c WHERE m.FROM = c.SEQ AND m.IMPORTANT = 1 AND m.DISTINGUISH = 'com' AND m.DEL = 0
AND( m.CONTENT LIKE CONCAT('%', #{sKeyword}, '%') OR c.NAME = #{sKeyword})
ORDER BY m.SDATE DESC LIMIT #{recordCountPerPage, jdbcType=INTEGER} OFFSET #{start, jdbcType=INTEGER}

mapper.update:
UPDATE MESSAGE 
SET DEL = 1
WHERE SEQ = ${seq}

mapper.select:
SELECT m.SEQ, m.CONTENT, m.FROM, m.TO, m.SDATE, m.OPEN, m.DEL, m.IMPORTANT, m.DISTINGUISH, c.NAME FROM MESSAGE m, C_Member c WHERE m.FROM = c.SEQ AND m.DISTINGUISH = 'mem' AND m.DEL = 0
AND( m.CONTENT LIKE CONCAT('%', #{sKeyword}, '%') OR c.NAME = #{sKeyword})
ORDER BY m.SDATE DESC LIMIT #{recordCountPerPage, jdbcType=INTEGER} OFFSET #{start, jdbcType=INTEGER}

mapper.update:
UPDATE MESSAGE
SET OPEN = 1
WHERE SEQ = #{seq}
