mapper.update:
UPDATE A_MEMBER
SET DEL = 0 
WHERE EMAIL = #{email}

UPDATE C_MEMBER
SET DEL = 0 
WHERE EMAIL = #{email}

mapper.select:
SELECT PASSWORD 
FROM A_MEMBER
WHERE SEQ = ${seq}

SELECT PASSWORD 
FROM C_MEMBER
WHERE SEQ = ${seq}

mapper.update:
UPDATE A_MEMBER
SET PASSWORD = #{password}
WHERE SEQ = ${seq}

UPDATE C_MEMBER
SET PASSWORD = #{password}
WHERE SEQ = ${seq}

UPDATE A_MEMBER
SET DEL = 1, SIGNDATE = NOW()
WHERE SEQ = ${seq}

UPDATE C_MEMBER
SET DEL = 1, SIGNDATE = NOW()
WHERE SEQ = ${seq}
